module.exports.sum = function(a,b) {return a + b};
module.exports.subtract = function(a,b) {return a - b};
module.exports.multiply = function(a,b) {return a*b};
module.exports.divide = function(a,b) {return a/b};
module.exports.raisetopower = function(a,b) {return a **b};
module.exports.remainder = function(a,b) {return a % b};